package com.example.rafael.braintreetest;

import android.app.Activity;
import android.content.Intent;
import android.os.Bundle;
import android.support.v7.app.AppCompatActivity;
import android.util.Log;
import android.view.View;
import android.widget.Button;

import com.braintreepayments.api.dropin.DropInActivity;
import com.braintreepayments.api.dropin.DropInRequest;
import com.braintreepayments.api.dropin.DropInResult;

public class MainActivity extends AppCompatActivity {

    public static int REQUEST_CODE = 100;
    private Button btnBraintree;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_main);

        btnBraintree = (Button) findViewById(R.id.btnBraintree);
        btnBraintree.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View v) {
                DropInRequest dropInRequest = new DropInRequest()
                        .clientToken("");
                startActivityForResult(dropInRequest.getIntent(MainActivity.this), REQUEST_CODE);
            }
        });
    }

    @Override
    protected void onActivityResult(int requestCode, int resultCode, Intent data) {
        Log.d("DEBUG", "where am I?");
        if (requestCode == REQUEST_CODE) {
            Log.d("DEBUG", "code? " + resultCode);
            if (resultCode == Activity.RESULT_OK) {
                DropInResult result = data.getParcelableExtra(DropInResult.EXTRA_DROP_IN_RESULT);
                Log.d("DEBUG", "result");
                // use the result to update your UI and send the payment method nonce to your server
            } else if (resultCode == Activity.RESULT_CANCELED) {
                Log.d("DEBUG", "CANCELED");
                // the user canceled
            } else {
                // handle errors here, an exception may be available in
                Log.d("DEBUG", "NOT SURE");
                Exception error = (Exception) data.getSerializableExtra(DropInActivity.EXTRA_ERROR);
                try {
                    throw error;
                } catch (Exception e) {
                    e.printStackTrace();
                }
            }
        }
    }
}
